import axios from 'axios';

const API_BASE_URL = 'https://fakestoreapi.com';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
});

const transformProduct = (product) => {
  const foodCategories = {
    "electronics": "Beverages",
    "jewelery": "Desserts", 
    "men's clothing": "Main Course",
    "women's clothing": "Appetizers"
  };
  const foodNames = {
    "Fjallraven - Foldsack No. 1 Backpack": "Gourmet Burger",
    "Mens Casual Premium Slim Fit T-Shirts": "Grilled Chicken",
    "Mens Cotton Jacket": "BBQ Ribs",
    "Mens Casual Slim Fit": "Fish & Chips",
    "John Hardy Women's Legends Naga Gold & Silver Dragon Station Chain Bracelet": "Chocolate Cake",
    "Solid Gold Petite Micropave": "Tiramisu",
    "White Gold Plated Princess": "Ice Cream Sundae",
    "Pierced Owl Rose Gold Plated Stainless Steel Double": "Fruit Salad",
    "WD 2TB Elements Portable External Hard Drive": "Fresh Juice",
    "SanDisk SSD PLUS 1TB Internal SSD": "Energy Drink",
    "Silicon Power 256GB SSD 3D NAND A55 SLC Cache Performance SATA III 2.5": "Coffee",
    "WD 4TB Gaming Drive Works with Playstation 4 Portable External Hard Drive": "Smoothie",
    "Acer SB220Q bi 21.5 inches Full HD (1920 x 1080) IPS Ultra-Thin": "Milkshake",
    "Samsung 49-Inch CHG90 144Hz Curved Gaming Monitor": "Hot Chocolate",
    "BIYLACLESEN Women's 3-in-1 Snowboard Jacket Winter Coats": "Caesar Salad",
    "Lock and Love Women's Removable Hooded Faux Leather Moto Biker Jacket": "Greek Salad",
    "Rain Jacket Women Windbreaker Striped Climbing Raincoats": "Caprese Salad",
    "MBJ Women's Solid Short Sleeve Boat Neck V": "Pasta Salad",
    "Opna Women's Short Sleeve Moisture": "Quinoa Bowl",
    "DANVOUY Womens T Shirt Casual Cotton Short": "Veggie Wrap"
  };

  return {
    id: product.id,
    title: foodNames[product.title] || product.title,
    price: Math.round(product.price * 1.5), 
    description: `Delicious ${foodNames[product.title] || product.title.toLowerCase()} made with fresh ingredients. Perfect for any meal!`,
    category: foodCategories[product.category] || "Main Course",
    image: product.image,
    rating: product.rating
  };
};

export const fetchProducts = async (limit = 20) => {
  try {
    const response = await api.get('/products', {
      params: { limit }
    });
    return response.data.map(transformProduct);
  } catch (error) {
    console.error('Error fetching products:', error);
    throw new Error('Failed to fetch products');
  }
};

export const fetchProductById = async (id) => {
  try {
    const response = await api.get(`/products/${id}`);
    return transformProduct(response.data);
  } catch (error) {
    console.error('Error fetching product:', error);
    throw new Error('Failed to fetch product');
  }
};

export const fetchCategories = async () => {
  try {
    const response = await api.get('/products/categories');
    const foodCategories = {
      "electronics": "Beverages",
      "jewelery": "Desserts", 
      "men's clothing": "Main Course",
      "women's clothing": "Appetizers"
    };
    return response.data.map(cat => foodCategories[cat] || cat);
  } catch (error) {
    console.error('Error fetching categories:', error);
    return ["Main Course", "Appetizers", "Desserts", "Beverages"];
  }
};
